import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Progress() {
  const [workouts, setWorkouts] = useState([]);
  const [progressData, setProgressData] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/workouts')
      .then(res => {
        setWorkouts(res.data);
        calculateProgress(res.data);
      })
      .catch(err => console.error(err));
  }, []);

  const calculateProgress = (data) => {
    let totalReps = 0;
    let totalSets = 0;
    let exerciseNames = new Set();
    let categories = new Set();

    data.forEach(entry => {
      totalReps += parseInt(entry.reps || 0);
      totalSets += parseInt(entry.sets || 0);
      exerciseNames.add(entry.exercise);
      if (entry.category) categories.add(entry.category);
    });

    setProgressData({
      exercisesDone: Array.from(exerciseNames),
      totalReps,
      totalSets,
      categories: Array.from(categories)
    });
  };

  return (
    <div className="p-8">
      <h2 className="text-3xl font-bold mb-6 text-center">Progress Tracker</h2>

      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h3 className="text-xl font-semibold mb-4">Summary</h3>
        <p><strong>Total Reps:</strong> {progressData.totalReps}</p>
        <p><strong>Total Sets:</strong> {progressData.totalSets}</p>
        <p><strong>Categories:</strong> {progressData.categories?.join(', ') || 'None'}</p>
      </div>

      <div className="bg-white shadow-md rounded-lg p-6">
        <h3 className="text-xl font-semibold mb-4">Exercises Done</h3>
        <ul className="list-disc pl-5">
          {progressData.exercisesDone?.map((exercise, idx) => (
            <li key={idx} className="mb-1">{exercise}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}
